  min-height: 60px;
  background: transparent;
  transition: 0.3s all;
  z-index: 999;
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
}

.header.active {
  background: #fff;
}

.header .container {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  position: relative;
}

.header .header-title {
  display: block;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  z-index: 22;
  transition: all .35s cubic-bezier(.3,.86,.36,.95);
  text-transform: lowercase;
  color: #C4C4C4 !important;
  text-decoration: none !important;
  white-space: nowrap;
}

