}

.home__feed__item__img {
  position: relative;
  display: flex;
  align-items: center;
  height: 100%;
  cursor: pointer;
  pointer-events: auto;
  will-change: transform;
}

.home__feed__item__img picture {
  width: 100%;
  height: 100%;
}

.home__feed__item__img img {
  height: 100%;
}

/* Label del proyecto - alineado a la izquierda */
.home__feed__item__label {
  position: absolute;
  left: 0;
  height: 100%;
}

.home__feed__item__label p {
  position: relative;
  margin: 0;
  margin-bottom: -10vh;
  padding-top: 10px;
  padding-bottom: 15px;
  color: #C4C4C4 !important;
}

.home__feed__item:first-child .home__feed__item__label p {
  margin-bottom: 14.4vh;
}

/* Mobile */
@media screen and (max-width: 576px) {
  .home__feed__item__label {
    display: none;
  }

  .home__feed__item {
    height: 36vh;
    padding: 1.5px 0;
  }

  .home__feed__item:not(:first-child) {
    margin-top: -4.5vh;
  }

  .title_current {
    position: fixed;
    bottom: 15px;
    left: 20px;
    opacity: 0;
    color: #C4C4C4;
  }

  .title_current.scroll {
    opacity: 1;
  }
}

@media screen and (min-width: 577px) {
  .title_current {
    display: none;
  }
}

/* ==========================================================================
   SECTION CONTENT - margin-top para páginas con header fixed
   Esto es CLAVE: tenue usa margin-top: 70px en .section.content
   ========================================================================== */

.section.content {
  margin-top: 70px;
}

/* ==========================================================================
   PAGE PROJECTS (Index)
   ========================================================================== */

.page.projects .feed-projects {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -0.5rem;
}

@media screen and (min-width: 992px) {
  .page.projects .feed-projects {
    min-height: calc(100vh - 170px);
  }
}

/* Tabla como tenue */
.page.projects .table {
  display: table;
  width: 100%;
  border-collapse: collapse;
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
  --bs-table-color: #C4C4C4;
}

.page.projects .thead,
.page.projects .tbody {
  display: table-row-group;
}

.page.projects .tr {
  display: table-row;
  cursor: pointer;
}

.page.projects .th,
.page.projects .td {
  display: table-cell;
  padding: 5px 0;
  text-align: left;
  color: #C4C4C4 !important;
}

/* Columnas 2 y 3 con white-space nowrap para que no se expandan */
.page.projects .td:nth-child(1) {
  width: 60%;
}

.page.projects .td:nth-child(2) {
  white-space: nowrap;
  width: 20%;
  text-align: center;
}

.page.projects .td:nth-child(3) {
  white-space: nowrap;
  width: 20%;
  text-align: center;
}

.page.projects .th {
  font-weight: 500;
}

.page.projects .tbody a {
  position: relative;
  text-decoration: none !important;
}

.page.projects .table a:hover .td {
  color: #808080 !important;
}

.page.projects .tbody .spacer {
  padding: 0;
}

/* Thumbnail */
.page.projects .thumbnail {
  position: sticky;
  top: 78px;
  width: 100%;
  height: calc(100vh - 170px);
}

.page.projects .thumbnail img {
  opacity: 1;
  transition: opacity 0.5s ease-in-out;
}

.page.projects .thumbnail img.fade-in {
  opacity: 1;
}

.page.projects .thumbnail.is-vertical img {
  max-height: calc(100vh - 170px);
  object-fit: cover;
}

/* Grid columns */
.page.projects .col-12 {
  width: 100%;
  padding: 0 0.5rem;
}

@media screen and (min-width: 768px) {
  .page.projects .col-md-6 {
    width: 50%;
    flex: 0 0 50%;
  }
}

/* ==========================================================================
   PAGE CONTACT (Info)
   ========================================================================== */

.page.contact .image img {
  width: 100%;
  height: auto;
}

.page.contact .row {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -0.5rem;
  justify-content: center;
}

.page.contact .col-12 {
  width: 100%;
  padding: 0 0.5rem;
}

@media screen and (min-width: 768px) {
  .page.contact .col-md-4 {
    width: 33.333%;
    flex: 0 0 33.333%;
  }

  .page.contact .col-md-5 {
    width: 41.666%;
    flex: 0 0 41.666%;
  }

  .page.contact .order-md-1 {
    order: 1;
  }

  .page.contact .order-md-2 {
    order: 2;
  }
}

.page.contact h1,
.page.contact .about .title h3,
.page.contact .about .title p {
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
  font-weight: 400;
}

.page.contact .content h1,
.page.contact .content h3 {
  color: #C4C4C4 !important;
  margin: 0 0 10px 0;
}

.page.contact .data {
  color: #C4C4C4;
}

.page.contact .data p {
  margin: 0;
  line-height: 1.6;
}

.page.contact .vias {
  list-style: none;
  padding: 0;
  margin: 0;
}

.page.contact .vias a {
  text-decoration: underline !important;
  color: #C4C4C4 !important;
}

.page.contact .vias a:hover {
  color: #808080 !important;
}

.page.contact .content a.link:hover {
  text-decoration: underline;
}

@media screen and (min-width: 992px) {
  .page.contact .content article {
    display: flex;
    gap: 30px;
  }
}

/* ==========================================================================
   SINGLE PROJECT
   ========================================================================== */

.single.projects .content .title {
  max-width: 900px;
  margin: 0 auto;
}

.single.projects .content .title h1 {
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
  margin-bottom: 0;
  font-weight: 700 !important;
}

/* Accordion */
.single.projects .accordion button {
  position: relative;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  width: 30px;
  height: 30px;
}

.single.projects .accordion button::after {
  content: '+';
  color: #C4C4C4;
  line-height: 20px;
  font-size: 24px;
  transition: 0.3s cubic-bezier(0.68, 0.17, 0.02, 0.86);
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
}

.single.projects .accordion button.active::after {
  content: '-';
}

.accordion-content {
  max-height: 0;
  overflow: hidden;
  transition: all 0.5s ease-in-out;
  opacity: 0;
  visibility: hidden;
}

.accordion-content.show {
  max-height: 3000px;
  opacity: 1;
  visibility: visible;
}

/* Gallery */
.single.projects .gallery .g-5 {
  --bs-gutter-x: 4rem;
  --bs-gutter-y: 4rem;
}

/* Nav Projects */
.nav-projects {
  border-top: none;
  margin-top: 80px;
  padding-bottom: 150px;
}

.nav-projects .row {
  display: flex !important;
  align-items: flex-start !important;
  justify-content: space-between;
}

.nav-link {
  text-decoration: none !important;
  color: #C4C4C4 !important;
  transition: all 0.3s;
  display: flex !important;
  flex-direction: column;
}

.nav-link.next {
  align-items: flex-end;
  text-align: right;
}

.nav-link.prev {
  align-items: flex-start;
  text-align: left;
}

.nav-link:hover {
  color: #808080 !important;
}

.nav-link .project-name,
.nav-link .arrow {
  border-bottom: 1px solid transparent;
  padding-bottom: 0px;
  line-height: 1.1;
  display: inline-block;
  width: fit-content;
}

.nav-link .project-name {
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
  text-transform: capitalize;
}

.nav-link .arrow {
  font-size: 20px;
  margin-top: 4px;
}

.nav-link:hover .project-name,
.nav-link:hover .arrow {
  border-bottom-color: #808080;
}

.nav-link.disabled {
  display: none; /* En navegación circular no debería haber deshabilitados, pero por si acaso */
}

.portfolio-item {
  will-change: transform;
  margin-bottom: 2rem !important; /* Espacio uniforme entre piezas */
}

/* Gallery */
.single.projects .gallery .img-single-project {
  width: 100%;
  height: auto;
  display: block;
}

/* ==========================================================================
   MAIN & FOOTER
   ========================================================================== */

.main {
  width: 100%;
  min-height: 80vh;
}

.footer {
  /* vacío como tenue */
}

/* ==========================================================================
   UTILITIES - Como Bootstrap/tenue
   ========================================================================== */

.list-unstyled {
  list-style: none;
  padding: 0;
  margin: 0;
}

.img-fluid {
  max-width: 100%;
  height: auto;
}

.w-100 {
  width: 100%;
}

.pt-0 {
  padding-top: 0 !important;
}

.pt-3 {
  padding-top: 1rem !important;
}

.pt-5 {
  padding-top: 3rem !important;
}

.pb-0 {
  padding-bottom: 0 !important;
}

.mb-0 {
  margin-bottom: 0 !important;
}

.mb-3 {
  margin-bottom: 1rem !important;
}

.g-3 {
  gap: 1rem;
}

.d-none {
  display: none !important;
}

@media screen and (min-width: 768px) {
  .d-md-block {
    display: block !important;
  }
}

.text-end {
  text-align: right !important;
}

.text-center {
  text-align: center !important;
}

.justify-content-center {
  justify-content: center !important;
}

.section {
  position: relative;
  padding: 30px 0;
}

@media screen and (min-width: 992px) {
  .section {
    padding: 60px 0;
  }
}

.clearfix::after {
  content: "";
  display: table;
  clear: both;
}

/* ==========================================================================
   ANIMATIONS
   ========================================================================== */

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes fadeInDown {
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInDown {
  from {
    transform: translateY(-100%);
  }
  to {
    transform: translateY(0);
  }
}

