                transitionDuration: "0.3s",
              });
            });
          }
        });
      });
    }

    return () => {
      Fancybox.destroy();
      if (msnry) msnry.destroy();
    };
  }, [project.slug, images]);

  const toggleCredits = () => {
    setShowCredits(!showCredits);
