@import "tailwindcss";

:root {
  --background: #fff;
  --foreground: #000;
  --color-text: #000000;
  --color-text-hover: #808080;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-text: var(--color-text);
  --font-sans: 'Inter', system-ui, sans-serif;
}

/* ==========================================================================
   BASE STYLES - Estilo tenue.uy exacto
   ========================================================================== */

html {
  scroll-behavior: smooth;
}

/* Scrollbar con fondo transparente */
html {
  scrollbar-width: thin;
  scrollbar-color: #444444 transparent;
}

::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: #C4C4C4;
  border-radius: 0;
}

::-webkit-scrollbar-thumb:hover {
  background: #808080;
}

body {
  background: #fff;
  color: #C4C4C4;
  font-family: 'Suisse Intl', 'Inter', system-ui, sans-serif;
  font-size: 16px;
  font-weight: 400;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body .main {
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
}

a,
a:hover {
  color: inherit;
  text-decoration: none;
  transition: 0.3s all;
}

/* ==========================================================================
   CONTAINER - Bootstrap-like
   ========================================================================== */

.container {
  width: 100%;
  max-width: 1600px;
  margin: 0 auto;
  padding: 0 1rem;
}

@media screen and (min-width: 1440px) {
  .container {
    padding: 0 2rem;
  }
}

/* ==========================================================================
   HEADER - Estilo tenue.uy exacto
   ========================================================================== */

.header {
  display: flex;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  min-height: 60px;
  background: transparent;
  transition: 0.3s all;
  z-index: 999;
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
}

.header.active {
  background: #fff;
}

.header .container {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  position: relative;
}

.header .header-title {
  display: block;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  z-index: 22;
  transition: all .35s cubic-bezier(.3,.86,.36,.95);
  text-transform: lowercase;
  color: #C4C4C4 !important;
  text-decoration: none !important;
  white-space: nowrap;
}

.header .header-title::after {
  content: '';
  display: block;
  border-bottom: solid 1px #C4C4C4;
  transform: scaleX(0);
  height: 2px;
  opacity: 1;
  transform-origin: 100% 0%;
  transition: transform 300ms ease-in-out;
}

.header .header-title:hover::after {
  transform: scaleX(1);
  transform-origin: 0% 100%;
}

/* Nav Menu hamburguesa (móvil) */
.header .nav-menu {
  position: relative;
  width: 28px;
  cursor: pointer;
  z-index: 21;
  display: block;
}

.header .nav-menu .menu-line {
  display: block;
  width: 100%;
  height: 2px;
  margin: 3px 0;
  background: #000;
  transition: 0.3s;
}

.header .nav-menu.active .menu-line:nth-child(1) {
  transform: rotate(45deg);
  width: 30px;
  margin-top: 12px;
}

.header .nav-menu.active .menu-line:nth-child(3) {
  transform: rotate(-45deg);
  width: 30px;
  margin-top: -14px;
}

.header .nav-menu.active .menu-line:nth-child(2) {
  opacity: 0;
}

/* Navigation móvil */
.header .navigation {
  display: none;
}

.header .navigation.active {
  display: block;
  position: fixed;
  top: 60px;
  left: 0;
  width: 100%;
  padding: 20px;
  background: #fff;
  animation: fadeIn 0.3s ease-out;
}

.header .navigation ul {
  margin: 0;
  padding: 0;
  list-style: none;
}

.header .navigation ul li a {
  position: relative;
  display: inline-block;
  padding: 10px 0;
  text-transform: lowercase;
  color: #C4C4C4 !important;
  text-decoration: none !important;
}

.header .navigation ul li a::after {
  content: '';
  display: block;
  border-bottom: solid 1px #C4C4C4;
  transform: scaleX(0);
  height: 2px;
  opacity: 1;
  transform-origin: 100% 0%;
  transition: transform 300ms ease-in-out;
}

.header .navigation ul li.current-menu-item a::after,
.header .navigation ul li a:hover::after {
  transform: scaleX(1);
  transform-origin: 0% 100%;
}

/* Desktop Header */
@media screen and (min-width: 992px) {
  .header {
    min-height: 78px;
  }

  .header .container {
    flex-direction: row;
    justify-content: center;
    position: relative;
  }

  .header .header-title {
    text-align: center;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    min-width: auto;
  }

  .header .nav-menu {
    display: none;
  }

  .header .navigation {
    display: flex;
    align-items: center;
    justify-content: space-between;
    position: relative;
    width: 100%;
    padding: 0;
    background: transparent;
  }

  .header .navigation ul {
    display: flex;
