@import "tailwindcss";

:root {
  --background: #fff;
  --foreground: #000;
  --color-text: #000000;
  --color-text-hover: #808080;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-text: var(--color-text);
  --font-sans: 'Inter', system-ui, sans-serif;
}

/* ==========================================================================
   BASE STYLES - Estilo tenue.uy exacto
   ========================================================================== */

html {
  scroll-behavior: smooth;
}

/* Scrollbar con fondo transparente */
html {
  scrollbar-width: thin;
  scrollbar-color: #444444 transparent;
}

::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: #C4C4C4;
  border-radius: 0;
}

::-webkit-scrollbar-thumb:hover {
  background: #808080;
}

body {
  background: #fff;
  color: #C4C4C4;
  font-family: 'Suisse Intl', 'Inter', system-ui, sans-serif;
  font-size: 16px;
  font-weight: 400;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body .main {
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
}

a,
a:hover {
  color: inherit;
  text-decoration: none;
  transition: 0.3s all;
}

/* ==========================================================================
   CONTAINER - Bootstrap-like
   ========================================================================== */

.container {
  width: 100%;
  max-width: 1600px;
  margin: 0 auto;
  padding: 0 1rem;
}

@media screen and (min-width: 1440px) {
  .container {
    padding: 0 2rem;
  }
}

/* ==========================================================================
   HEADER - Estilo tenue.uy exacto
   ========================================================================== */

.header {
  display: flex;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  min-height: 60px;
  background: transparent;
  transition: 0.3s all;
  z-index: 999;
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
}

.header.active {
  background: #fff;
}

.header .container {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  position: relative;
}

.header .header-title {
  display: block;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  z-index: 22;
  transition: all .35s cubic-bezier(.3,.86,.36,.95);
  text-transform: lowercase;
  color: #C4C4C4 !important;
  text-decoration: none !important;
  white-space: nowrap;
}

.header .header-title::after {
  content: '';
  display: block;
  border-bottom: solid 1px #C4C4C4;
  transform: scaleX(0);
  height: 2px;
  opacity: 1;
  transform-origin: 100% 0%;
  transition: transform 300ms ease-in-out;
}

.header .header-title:hover::after {
  transform: scaleX(1);
  transform-origin: 0% 100%;
}

/* Nav Menu hamburguesa (móvil) */
.header .nav-menu {
  position: relative;
  width: 28px;
  cursor: pointer;
  z-index: 21;
  display: block;
}

.header .nav-menu .menu-line {
  display: block;
  width: 100%;
  height: 2px;
  margin: 3px 0;
  background: #000;
  transition: 0.3s;
}

.header .nav-menu.active .menu-line:nth-child(1) {
  transform: rotate(45deg);
  width: 30px;
  margin-top: 12px;
}

.header .nav-menu.active .menu-line:nth-child(3) {
  transform: rotate(-45deg);
  width: 30px;
  margin-top: -14px;
}

.header .nav-menu.active .menu-line:nth-child(2) {
  opacity: 0;
}

/* Navigation móvil */
.header .navigation {
  display: none;
}

.header .navigation.active {
  display: block;
  position: fixed;
  top: 60px;
  left: 0;
  width: 100%;
  padding: 20px;
  background: #fff;
  animation: fadeIn 0.3s ease-out;
}

.header .navigation ul {
  margin: 0;
  padding: 0;
  list-style: none;
}

.header .navigation ul li a {
  position: relative;
  display: inline-block;
  padding: 10px 0;
  text-transform: lowercase;
  color: #C4C4C4 !important;
  text-decoration: none !important;
}

.header .navigation ul li a::after {
  content: '';
  display: block;
  border-bottom: solid 1px #C4C4C4;
  transform: scaleX(0);
  height: 2px;
  opacity: 1;
  transform-origin: 100% 0%;
  transition: transform 300ms ease-in-out;
}

.header .navigation ul li.current-menu-item a::after,
.header .navigation ul li a:hover::after {
  transform: scaleX(1);
  transform-origin: 0% 100%;
}

/* Desktop Header */
@media screen and (min-width: 992px) {
  .header {
    min-height: 78px;
  }

  .header .container {
    flex-direction: row;
    justify-content: center;
    position: relative;
  }

  .header .header-title {
    text-align: center;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    min-width: auto;
  }

  .header .nav-menu {
    display: none;
  }

  .header .navigation {
    display: flex;
    align-items: center;
    justify-content: space-between;
    position: relative;
    width: 100%;
    padding: 0;
    background: transparent;
  }

  .header .navigation ul {
    display: flex;
    justify-content: space-between;
    width: 35%;
  }

  .header .navigation ul.menu-left {
    justify-content: flex-start;
  }

  .header .navigation ul.menu-right {
    justify-content: flex-end;
  }

  .header .navigation ul li a {
    display: block;
    padding: 10px 0;
  }
}

/* ==========================================================================
   HOME SCROLL CONTAINER - Como tenue
   ========================================================================== */

.home-scroll {
  height: 100vh;
  overflow-x: hidden;
  overflow-y: scroll;
}

@media screen and (max-width: 576px) {
  .home-scroll {
    height: 100svh;
  }
}

/* ==========================================================================
   PRESENTATION (Logo inicial home)
   ========================================================================== */

.section.presentation {
  position: fixed !important;
  top: 0;
  left: 0;
  width: 100%;
  height: 100dvh;
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: none;
  z-index: 10;
  padding: 0;
}

.presentation-spacer {
  height: 100dvh;
}

.section.presentation .container {
  max-width: 100%;
  padding: 0;
}

.presentation .logo {
  transition: opacity 0.3s ease;
  opacity: 1;
  width: 100%;
  max-width: 100%;
  padding: 0;
}

.presentation .logo img {
  width: 100%;
  height: auto;
  display: block;
}

.presentation .logo.scroll {
  opacity: 0;
}

/* ==========================================================================
   HOME FEED - Estilo tenue.uy exacto
   ========================================================================== */

.home__feed {
  display: flex;
  flex-flow: column;
  pointer-events: none;
}

@media (max-width: 656px) {
  .home__feed {
    padding-top: 14px;
  }
}

.home__feed__item {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 75vh;
  padding: 3px 0;
  box-sizing: border-box;
  perspective: 800px;
}

.home__feed__item:first-child {
  margin-top: 50px;
}

.home__feed__item:not(:first-child) {
  margin-top: -20vh;
}

.home__feed__item__img {
  position: relative;
  display: flex;
  align-items: center;
  height: 100%;
  cursor: pointer;
  pointer-events: auto;
  will-change: transform;
}

.home__feed__item__img picture {
  width: 100%;
  height: 100%;
}

.home__feed__item__img img {
  height: 100%;
}

/* Label del proyecto - alineado a la izquierda */
.home__feed__item__label {
  position: absolute;
  left: 0;
  height: 100%;
}

.home__feed__item__label p {
  position: relative;
  margin: 0;
  margin-bottom: -10vh;
  padding-top: 10px;
  padding-bottom: 15px;
  color: #C4C4C4 !important;
}

.home__feed__item:first-child .home__feed__item__label p {
  margin-bottom: 14.4vh;
}

/* Mobile */
@media screen and (max-width: 576px) {
  .home__feed__item__label {
    display: none;
  }

  .home__feed__item {
    height: 36vh;
    padding: 1.5px 0;
  }

  .home__feed__item:not(:first-child) {
    margin-top: -4.5vh;
  }

  .title_current {
    position: fixed;
    bottom: 15px;
    left: 20px;
    opacity: 0;
    color: #C4C4C4;
  }

  .title_current.scroll {
    opacity: 1;
  }
}

@media screen and (min-width: 577px) {
  .title_current {
    display: none;
  }
}

/* ==========================================================================
   SECTION CONTENT - margin-top para páginas con header fixed
   Esto es CLAVE: tenue usa margin-top: 70px en .section.content
   ========================================================================== */

.section.content {
  margin-top: 70px;
}

/* ==========================================================================
   PAGE PROJECTS (Index)
   ========================================================================== */

.page.projects .feed-projects {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -0.5rem;
}

@media screen and (min-width: 992px) {
  .page.projects .feed-projects {
    min-height: calc(100vh - 170px);
  }
}

/* Tabla como tenue */
.page.projects .table {
  display: table;
  width: 100%;
  border-collapse: collapse;
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
  --bs-table-color: #C4C4C4;
}

.page.projects .thead,
.page.projects .tbody {
  display: table-row-group;
}

.page.projects .tr {
  display: table-row;
  cursor: pointer;
}

.page.projects .th,
.page.projects .td {
  display: table-cell;
  padding: 5px 0;
  text-align: left;
  color: #C4C4C4 !important;
}

/* Columnas 2 y 3 con white-space nowrap para que no se expandan */
.page.projects .td:nth-child(1) {
  width: 60%;
}

.page.projects .td:nth-child(2) {
  white-space: nowrap;
  width: 20%;
  text-align: center;
}

.page.projects .td:nth-child(3) {
  white-space: nowrap;
  width: 20%;
  text-align: center;
}

.page.projects .th {
  font-weight: 500;
}

.page.projects .tbody a {
  position: relative;
  text-decoration: none !important;
}

.page.projects .table a:hover .td {
  color: #808080 !important;
}

.page.projects .tbody .spacer {
  padding: 0;
}

/* Thumbnail */
.page.projects .thumbnail {
  position: sticky;
  top: 78px;
  width: 100%;
  height: calc(100vh - 170px);
}

.page.projects .thumbnail img {
  opacity: 1;
  transition: opacity 0.5s ease-in-out;
}

.page.projects .thumbnail img.fade-in {
  opacity: 1;
}

.page.projects .thumbnail.is-vertical img {
  max-height: calc(100vh - 170px);
  object-fit: cover;
}

/* Grid columns */
.page.projects .col-12 {
  width: 100%;
  padding: 0 0.5rem;
}

@media screen and (min-width: 768px) {
  .page.projects .col-md-6 {
    width: 50%;
    flex: 0 0 50%;
  }
}

/* ==========================================================================
   PAGE CONTACT (Info)
   ========================================================================== */

.page.contact .image img {
  width: 100%;
  height: auto;
}

.page.contact .row {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -0.5rem;
  justify-content: center;
}

.page.contact .col-12 {
  width: 100%;
  padding: 0 0.5rem;
}

@media screen and (min-width: 768px) {
  .page.contact .col-md-4 {
    width: 33.333%;
    flex: 0 0 33.333%;
  }

  .page.contact .col-md-5 {
    width: 41.666%;
    flex: 0 0 41.666%;
  }

  .page.contact .order-md-1 {
    order: 1;
  }

  .page.contact .order-md-2 {
    order: 2;
  }
}

.page.contact h1,
.page.contact .about .title h3,
.page.contact .about .title p {
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
  font-weight: 400;
}

.page.contact .content h1,
.page.contact .content h3 {
  color: #C4C4C4 !important;
  margin: 0 0 10px 0;
}

.page.contact .data {
  color: #C4C4C4;
}

.page.contact .data p {
  margin: 0;
  line-height: 1.6;
}

.page.contact .vias {
  list-style: none;
  padding: 0;
  margin: 0;
}

.page.contact .vias a {
  text-decoration: underline !important;
  color: #C4C4C4 !important;
}

.page.contact .vias a:hover {
  color: #808080 !important;
}

.page.contact .content a.link:hover {
  text-decoration: underline;
}

@media screen and (min-width: 992px) {
  .page.contact .content article {
    display: flex;
    gap: 30px;
  }
}

/* ==========================================================================
   SINGLE PROJECT
   ========================================================================== */

/* SINGLE PROJECT Header */
.single.projects .content .title h1 {
  font-size: clamp(16px, 0.278vw + 0.938rem, 19px);
  margin-bottom: 0;
  font-weight: 500 !important;
}

/* Accordion */
.single.projects .accordion button {
  position: relative;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  width: 30px;
  height: 30px;
}

