  left: 0;
  width: 100%;
  height: 100dvh;
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: none;
  z-index: 10;
  padding: 0;
}

.presentation-spacer {
  height: 100dvh;
}

.section.presentation .container {
  max-width: 100%;
  padding: 0;
}

.presentation .logo {
  transition: opacity 0.3s ease;
  opacity: 1;
  width: 100%;
  max-width: 100%;
  padding: 0;
}

.presentation .logo img {
  width: 100%;
  height: auto;
  display: block;
}

.presentation .logo.scroll {
  opacity: 0;
}

/* ==========================================================================
   HOME FEED - Estilo tenue.uy exacto
   ========================================================================== */

.home__feed {
  display: flex;
  flex-flow: column;
  pointer-events: none;
}

@media (max-width: 656px) {
  .home__feed {
    padding-top: 14px;
  }
}

.home__feed__item {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 75vh;
  padding: 3px 0;
  box-sizing: border-box;
  perspective: 800px;
}

.home__feed__item:first-child {
  margin-top: 50px;
}

.home__feed__item:not(:first-child) {
  margin-top: -20vh;
}

.home__feed__item__img {
  position: relative;
  display: flex;
  align-items: center;
  height: 100%;
  cursor: pointer;
  pointer-events: auto;
  will-change: transform;
}

.home__feed__item__img picture {
  width: 100%;
  height: 100%;
}

.home__feed__item__img img {
  height: 100%;
}

/* Label del proyecto - alineado a la izquierda */
.home__feed__item__label {
  position: absolute;
  left: 0;
  height: 100%;
}

.home__feed__item__label p {
  position: relative;
  margin: 0;
  margin-bottom: -10vh;
  padding-top: 10px;
  padding-bottom: 15px;
  color: #C4C4C4 !important;
}

.home__feed__item:first-child .home__feed__item__label p {
  margin-bottom: 14.4vh;
}

/* Mobile */
@media screen and (max-width: 576px) {
  .home__feed__item__label {
    display: none;
  }

  .home__feed__item {
    height: 36vh;
    padding: 1.5px 0;
  }

  .home__feed__item:not(:first-child) {
    margin-top: -4.5vh;
  }

  .title_current {
    position: fixed;
    bottom: 15px;
    left: 20px;
    opacity: 0;
    color: #C4C4C4;
  }

  .title_current.scroll {
    opacity: 1;
  }
}

@media screen and (min-width: 577px) {
  .title_current {
    display: none;
  }
}

