  width: 60%;
}

.page.projects .td:nth-child(2) {
  white-space: nowrap;
  width: 20%;
  text-align: center;
}

.page.projects .td:nth-child(3) {
  white-space: nowrap;
  width: 20%;
  text-align: center;
}

.page.projects .th {
  font-weight: 500;
}

.page.projects .tbody a {
  position: relative;
  text-decoration: none !important;
}

.page.projects .table a:hover .td {
  color: #808080 !important;
}

.page.projects .tbody .spacer {
  padding: 0;
