                        className="text-sm text-red-500 hover:text-red-700 transition-colors cursor-pointer disabled:opacity-50"
                      >
                        Eliminar
                      </button>
                      <a
                        href={`/projects/${project.slug}`}
                        target="_blank"
                        className="text-sm text-concrete hover:text-carbon transition-colors"
                      >
                        Ver →
                      </a>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </main>
    </>
  );
}

import { NextRequest, NextResponse } from 'next/server';
import fs from 'fs/promises';
import path from 'path';

const PROJECTS_FILE = path.join(process.cwd(), 'content', 'projects.json');

// GET - Obtener todos los proyectos
export async function GET() {
  try {
    const data = await fs.readFile(PROJECTS_FILE, 'utf-8');
    const projects = JSON.parse(data);
    return NextResponse.json(projects);
  } catch (error) {
    return NextResponse.json({ error: 'Error al leer proyectos' }, { status: 500 });
  }
}

// POST - Crear nuevo proyecto
export async function POST(request: NextRequest) {
  try {
    const newProject = await request.json();

    // Leer proyectos existentes
    const data = await fs.readFile(PROJECTS_FILE, 'utf-8');
    const { projects } = JSON.parse(data);

    // Verificar que no exista un proyecto con el mismo slug
    if (projects.find((p: { slug: string }) => p.slug === newProject.slug)) {
      return NextResponse.json({ error: 'Ya existe un proyecto con ese slug' }, { status: 400 });
    }

    // Crear carpeta para imágenes del proyecto
    const projectImagesDir = path.join(process.cwd(), 'public', 'projects', newProject.slug);
    await fs.mkdir(projectImagesDir, { recursive: true });

    // Agregar proyecto
    projects.push(newProject);

    // Guardar
    await fs.writeFile(PROJECTS_FILE, JSON.stringify({ projects }, null, 2), 'utf-8');

    return NextResponse.json({ success: true, project: newProject });
  } catch (error) {
    console.error('Error creating project:', error);
    return NextResponse.json({ error: 'Error al crear proyecto' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import fs from 'fs/promises';
import path from 'path';

const PROJECTS_FILE = path.join(process.cwd(), 'content', 'projects.json');

type RouteParams = { params: Promise<{ slug: string }> };

// GET - Obtener un proyecto por slug
export async function GET(request: NextRequest, { params }: RouteParams) {
  try {
    const { slug } = await params;
    const data = await fs.readFile(PROJECTS_FILE, 'utf-8');
    const { projects } = JSON.parse(data);

    const project = projects.find((p: { slug: string }) => p.slug === slug);

    if (!project) {
      return NextResponse.json({ error: 'Proyecto no encontrado' }, { status: 404 });
    }

    return NextResponse.json(project);
  } catch (error) {
    return NextResponse.json({ error: 'Error al leer proyecto' }, { status: 500 });
  }
}

// PUT - Actualizar proyecto
export async function PUT(request: NextRequest, { params }: RouteParams) {
  try {
    const { slug } = await params;
    const updatedData = await request.json();

    const data = await fs.readFile(PROJECTS_FILE, 'utf-8');
    const { projects } = JSON.parse(data);

    const index = projects.findIndex((p: { slug: string }) => p.slug === slug);

    if (index === -1) {
      return NextResponse.json({ error: 'Proyecto no encontrado' }, { status: 404 });
    }

    // Actualizar proyecto manteniendo el slug original
    projects[index] = { ...projects[index], ...updatedData, slug };

    await fs.writeFile(PROJECTS_FILE, JSON.stringify({ projects }, null, 2), 'utf-8');

    return NextResponse.json({ success: true, project: projects[index] });
  } catch (error) {
    console.error('Error updating project:', error);
    return NextResponse.json({ error: 'Error al actualizar proyecto' }, { status: 500 });
  }
}

// DELETE - Eliminar proyecto
export async function DELETE(request: NextRequest, { params }: RouteParams) {
  try {
    const { slug } = await params;
    const data = await fs.readFile(PROJECTS_FILE, 'utf-8');
    const { projects } = JSON.parse(data);

    const index = projects.findIndex((p: { slug: string }) => p.slug === slug);

    if (index === -1) {
      return NextResponse.json({ error: 'Proyecto no encontrado' }, { status: 404 });
    }

    // Eliminar proyecto del array
    const deleted = projects.splice(index, 1)[0];

    // Guardar
    await fs.writeFile(PROJECTS_FILE, JSON.stringify({ projects }, null, 2), 'utf-8');

    // Opcionalmente eliminar carpeta de imágenes
    const projectImagesDir = path.join(process.cwd(), 'public', 'projects', slug);
    try {
      await fs.rm(projectImagesDir, { recursive: true });
    } catch {
      // Ignorar si la carpeta no existe
    }

    return NextResponse.json({ success: true, deleted });
  } catch (error) {
    console.error('Error deleting project:', error);
    return NextResponse.json({ error: 'Error al eliminar proyecto' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import fs from 'fs/promises';
import path from 'path';

type RouteParams = { params: Promise<{ slug: string }> };

// POST - Subir imágenes a un proyecto
export async function POST(request: NextRequest, { params }: RouteParams) {
  try {
    const { slug } = await params;
    const formData = await request.formData();

    const projectDir = path.join(process.cwd(), 'public', 'projects', slug);

    // Asegurar que la carpeta existe
    await fs.mkdir(projectDir, { recursive: true });

    const uploadedFiles: string[] = [];

    // Procesar cada archivo
    for (const [key, value] of formData.entries()) {
      if (value instanceof File) {
        const buffer = Buffer.from(await value.arrayBuffer());
        const filename = value.name.replace(/[^a-zA-Z0-9.-]/g, '_');
        const filepath = path.join(projectDir, filename);

        await fs.writeFile(filepath, buffer);
        uploadedFiles.push(filename);
      }
    }

    return NextResponse.json({ 
      success: true, 
      files: uploadedFiles,
      message: `${uploadedFiles.length} archivos subidos` 
    });
  } catch (error) {
    console.error('Error uploading images:', error);
    return NextResponse.json({ error: 'Error al subir imágenes' }, { status: 500 });
  }
}

// GET - Listar imágenes de un proyecto
export async function GET(request: NextRequest, { params }: RouteParams) {
  try {
    const { slug } = await params;
    const projectDir = path.join(process.cwd(), 'public', 'projects', slug);

    try {
      const files = await fs.readdir(projectDir);
      const images = files.filter(f => /\.(jpg|jpeg|png|webp|gif)$/i.test(f));
      return NextResponse.json({ images });
    } catch {
      return NextResponse.json({ images: [] });
    }
  } catch (error) {
    return NextResponse.json({ error: 'Error al listar imágenes' }, { status: 500 });
  }
}

import fs from 'fs';
import path from 'path';

export type ProjectCredits = {
  proyecto?: string | null;
  equipo?: string | null;
  obra?: string | null;
  paisajismo?: string | null;
  interiorismo?: string | null;
  instalaciones?: string | null;
  estructura?: string | null;
  fotografias?: string | null;
};

export type Project = {
  slug: string;
  title: string;
  m2: number;
  status: "Construido" | "Proyecto";
  year: number;
  location: string;
  credits: ProjectCredits;
  exteriorImages: string[];
  interiorImages: string[];
  coverImage?: string;
};

// Leer proyectos desde el JSON
export function getProjects(): Project[] {
  const filePath = path.join(process.cwd(), 'content', 'projects.json');
  const fileContents = fs.readFileSync(filePath, 'utf8');
  const data = JSON.parse(fileContents);
  return data.projects;
}

// Obtener proyectos ordenados por año (descendente)
export function getProjectsSortedByYear(): Project[] {
  const projects = getProjects();
  return projects.sort((a, b) => b.year - a.year);
}

// Obtener proyectos en orden pseudo-aleatorio consistente
export function getProjectsShuffled(): Project[] {
  const projects = getProjects();

  // Usamos una función hash simple basada en los slugs para generar orden consistente
  const hashString = (str: string): number => {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      const char = str.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash;
    }
    return Math.abs(hash);
  };

  // Crear seed basado en todos los slugs concatenados
  const seed = hashString(projects.map(p => p.slug).join(''));

  // Shuffle determinístico usando el seed
  const shuffled = [...projects];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = (seed + i * 31) % (i + 1);
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }

  return shuffled;
}

// Obtener un proyecto por slug
export function getProjectBySlug(slug: string): Project | undefined {
  const projects = getProjects();
  return projects.find(p => p.slug === slug);
}

// Obtener proyecto anterior y siguiente (para navegación circular)
export function getAdjacentProjects(slug: string): { prev: Project | null; next: Project | null } {
  const projects = getProjectsSortedByYear();
  const currentIndex = projects.findIndex(p => p.slug === slug);

  if (currentIndex === -1) return { prev: null, next: null };

  const prevIndex = (currentIndex - 1 + projects.length) % projects.length;
  const nextIndex = (currentIndex + 1) % projects.length;

  return {
    prev: projects[prevIndex],
    next: projects[nextIndex],
  };
}

// Obtener la imagen de portada de un proyecto
export function getProjectCoverImage(project: Project): string {
  // Primero intentamos usar cover.jpg, si no existe usamos la primera imagen exterior
  const basePath = `/projects/${project.slug}`;

  // Por defecto usamos la primera imagen exterior como cover
  if (project.exteriorImages.length > 0) {
    return `${basePath}/exterior/${project.exteriorImages[0]}`;
  }

  // Fallback a imagen placeholder
  return 'https://images.unsplash.com/photo-1600585154340-be6161a56a0c?w=1600&q=80';
}

// Obtener todas las imágenes de un proyecto (exterior primero, luego interior)
export function getProjectImages(project: Project): { src: string; type: 'exterior' | 'interior' }[] {
  const basePath = `/projects/${project.slug}`;
  const images: { src: string; type: 'exterior' | 'interior' }[] = [];

  // Primero exterior
  project.exteriorImages.forEach(img => {
    images.push({ src: `${basePath}/exterior/${img}`, type: 'exterior' });
  });

  // Luego interior
  project.interiorImages.forEach(img => {
    images.push({ src: `${basePath}/interior/${img}`, type: 'interior' });
  });

  return images;
}

