  title: string;
  m2: number;
  status: "Construido" | "Proyecto";
  year: number;
  location: string;
  credits: ProjectCredits;
};

const emptyForm: ProjectForm = {
  slug: "",
  title: "",
  m2: 0,
  status: "Proyecto",
  year: new Date().getFullYear(),
  location: "",
  credits: {
    proyecto: "Noreste Arquitectura",
    equipo: "",
    obra: "",
    paisajismo: "",
    interiorismo: "",
    instalaciones: "",
    estructura: "",
    fotografias: "",
  },
};

export default function AdminPage() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);

  // Lista de proyectos
  const [projects, setProjects] = useState<Project[]>([]);
  const [editingSlug, setEditingSlug] = useState<string | null>(null);

  // Formulario
  const [form, setForm] = useState<ProjectForm>(emptyForm);
  const [images, setImages] = useState<File[]>([]);

  // Cargar proyectos
  const loadProjects = async () => {
    try {
      const res = await fetch("/api/projects");
      const data = await res.json();
      setProjects(data.projects || []);
    } catch (e) {
      console.error("Error loading projects:", e);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      loadProjects();
    }
  }, [isAuthenticated]);

  // Autenticación
  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (password === "norestearq2024") {
      setIsAuthenticated(true);
      setError("");
    } else {
      setError("Contraseña incorrecta");
    }
  };

  // Generar slug desde título
  const generateSlug = (title: string) => {
    return title
      .toLowerCase()
      .normalize("NFD")
      .replace(/[\u0300-\u036f]/g, "")
      .replace(/[^a-z0-9]+/g, "-")
      .replace(/^-+|-+$/g, "");
  };

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const title = e.target.value;
    setForm({
      ...form,
      title,
      slug: editingSlug ? form.slug : generateSlug(title),
    });
  };

  const handleCreditChange = (key: keyof ProjectCredits, value: string) => {
    setForm({
      ...form,
      credits: {
        ...form.credits,
        [key]: value || null,
      }
    });
  };

  // Editar proyecto existente
  const handleEdit = (project: Project) => {
    setEditingSlug(project.slug);
    setForm({
      slug: project.slug,
      title: project.title,
      m2: project.m2,
      status: project.status,
      year: project.year,
      location: project.location,
      credits: {
        proyecto: project.credits.proyecto || "",
        equipo: project.credits.equipo || "",
        obra: project.credits.obra || "",
        paisajismo: project.credits.paisajismo || "",
        interiorismo: project.credits.interiorismo || "",
        instalaciones: project.credits.instalaciones || "",
        estructura: project.credits.estructura || "",
        fotografias: project.credits.fotografias || "",
      },
    });
    setImages([]);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // Eliminar proyecto
  const handleDelete = async (slug: string) => {
    if (!confirm(`¿Eliminar el proyecto "${slug}"? Esta acción no se puede deshacer.`)) {
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(`/api/projects/${slug}`, { method: "DELETE" });
      if (res.ok) {
        setSuccess("Proyecto eliminado correctamente");
        loadProjects();
      } else {
        const data = await res.json();
        setError(data.error || "Error al eliminar");
      }
    } catch (e) {
      setError("Error de conexión");
    }
    setLoading(false);
  };

  // Crear o actualizar proyecto
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    const projectData = {
      ...form,
      exteriorImages: editingSlug ? projects.find(p => p.slug === editingSlug)?.exteriorImages || [] : [],
      interiorImages: editingSlug ? projects.find(p => p.slug === editingSlug)?.interiorImages || [] : [],
    };

    try {
      let res;

      if (editingSlug) {
        res = await fetch(`/api/projects/${editingSlug}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(projectData),
        });
      } else {
        res = await fetch("/api/projects", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(projectData),
        });
      }

      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.error || "Error al guardar");
      }

      // Subir imágenes si hay
      if (images.length > 0) {
        const formData = new FormData();
        images.forEach((img) => {
          formData.append("images", img);
        });

        const imgRes = await fetch(`/api/projects/${form.slug}/images`, {
          method: "POST",
          body: formData,
        });

        if (!imgRes.ok) {
          setError("Proyecto guardado pero hubo error subiendo imágenes");
        }
      }

      setSuccess(editingSlug ? "Proyecto actualizado correctamente" : "Proyecto creado correctamente");
      setForm(emptyForm);
      setImages([]);
      setEditingSlug(null);
      loadProjects();

    } catch (e) {
      setError(e instanceof Error ? e.message : "Error desconocido");
    }

    setLoading(false);
  };

  const handleCancel = () => {
    setForm(emptyForm);
    setImages([]);
    setEditingSlug(null);
    setError("");
    setSuccess("");
  };

  if (!isAuthenticated) {
    return (
      <main className="min-h-screen bg-[#fff] flex items-center justify-center p-6">
        <div className="w-full max-w-sm">
          <h1 className="text-xl text-[#C4C4C4] mb-8 text-center lowercase tracking-widest">noreste arch admin</h1>
          <form onSubmit={handleLogin} className="space-y-6">
            <div>
              <input
                type="password"
                value={password}
                placeholder="contraseña"
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-0 py-2 border-b border-[#C4C4C4]/30 bg-transparent text-[#000] focus:outline-none focus:border-[#C4C4C4] transition-colors placeholder:text-[#C4C4C4]/50"
              />
            </div>
            {error && <p className="text-red-400 text-xs">{error}</p>}
            <button
              type="submit"
              className="w-full py-3 border border-[#C4C4C4] text-[#C4C4C4] hover:bg-[#C4C4C4] hover:text-white transition-all cursor-pointer lowercase tracking-widest text-sm"
            >
              entrar
            </button>
          </form>
        </div>
      </main>
    );
  }

  return (
    <>
      <Header />
      <main className="min-h-screen bg-[#fff] pt-32 px-6 pb-20">
        <div className="max-w-5xl mx-auto">
          <div className="flex justify-between items-end mb-12 border-b border-[#C4C4C4]/20 pb-4">
            <h1 className="text-2xl text-[#000] lowercase tracking-widest font-medium">
