              <button onClick={handleCancel} className="text-[#C4C4C4] hover:text-[#000] text-sm lowercase transition-colors">
                cancelar edición
              </button>
            )}
          </div>

          {error && <div className="mb-8 p-4 bg-red-50 text-red-500 text-sm">{error}</div>}
          {success && <div className="mb-8 p-4 bg-gray-50 text-[#808080] text-sm">{success}</div>}

          <form onSubmit={handleSubmit} className="space-y-12">
            {/* Datos Básicos */}
            <section className="grid grid-cols-1 md:grid-cols-3 gap-x-12 gap-y-8">
              <div className="md:col-span-3">
                <h2 className="text-[#C4C4C4] text-xs uppercase tracking-widest mb-4">información general</h2>
              </div>

              <div className="space-y-1">
                <label className="text-[10px] text-[#C4C4C4] uppercase tracking-widest">título</label>
                <input
                  type="text"
                  value={form.title}
                  onChange={handleTitleChange}
                  required
                  className="w-full py-2 border-b border-[#C4C4C4]/30 bg-transparent text-[#000] focus:outline-none focus:border-[#000] transition-colors"
                />
              </div>

              <div className="space-y-1">
                <label className="text-[10px] text-[#C4C4C4] uppercase tracking-widest">slug (url)</label>
                <input
                  type="text"
                  value={form.slug}
                  onChange={(e) => setForm({ ...form, slug: e.target.value })}
                  disabled={!!editingSlug}
                  className="w-full py-2 border-b border-[#C4C4C4]/30 bg-transparent text-[#000] focus:outline-none disabled:opacity-30"
                />
              </div>

              <div className="space-y-1">
                <label className="text-[10px] text-[#C4C4C4] uppercase tracking-widest">estado</label>
                <select
                  value={form.status}
                  onChange={(e) => setForm({ ...form, status: e.target.value as any })}
                  className="w-full py-2 border-b border-[#C4C4C4]/30 bg-transparent text-[#000] focus:outline-none focus:border-[#000] cursor-pointer"
                >
                  <option value="Proyecto">Proyecto</option>
                  <option value="Construido">Construido</option>
                </select>
              </div>

              <div className="space-y-1">
                <label className="text-[10px] text-[#C4C4C4] uppercase tracking-widest">m2</label>
                <input
