                  onChange={(e) => setImages(Array.from(e.target.files || []))}
                  className="hidden"
                />
                <label 
                  htmlFor="file-upload" 
                  className="w-full md:w-fit px-8 py-4 border border-dashed border-[#C4C4C4] text-[#C4C4C4] text-center cursor-pointer hover:border-[#000] hover:text-[#000] transition-all"
