                    <div className="flex items-center gap-8">
                      <button
                        onClick={() => handleEdit(project)}
                        className="text-xs text-[#C4C4C4] hover:text-[#000] transition-colors uppercase tracking-widest"
                      >
                        editar
                      </button>
                      <button
                        onClick={() => handleDelete(project.slug)}
                        disabled={loading}
                        className="text-xs text-red-300 hover:text-red-500 transition-colors uppercase tracking-widest disabled:opacity-30"
